import { useState } from 'react';
import './App.css';
import Background from './Components/Background';
import MainBody from './Components/Main Body';
import TopBar from './Components/TopBar';
import {ThemeProvider} from 'styled-components'

function App() {

  const [isItDarkMode, setDarkMode] = useState(false)
  // console.log(classes)

  const [theme, setTheme] = useState('light')

  const LightTheme = {
    pageBackground: 'white',
    titleColor: 'black'
  }

  const DarkTheme = {
    pageBackground: 'black',
    titleColor: 'white'
  }

  const themes = {
    light: LightTheme,
    dark: DarkTheme
  }

  return (
    <ThemeProvider theme={themes[theme]}>
      <Background/>
      <TopBar darkMode={isItDarkMode} setDarkMode={setDarkMode}/>
      <MainBody/>
    </ThemeProvider>
  );
}

export default App;
