import React, { useState } from "react";

import classes from './style.module.css'

const Form = () => {

    const submitHandler = (e) => {
        e.preventDefault()
        setIsTouched(false)
    }

    const [isTouched, setIsTouched] = useState(false)

    const onTouchHandler = () => {
        setIsTouched(true)
    }

    return <div>
        <form onSubmit={submitHandler}>
            <input className={classes.inputField} type="url" placeholder="Enter product link" onClick={onTouchHandler}/>
            {isTouched && <input className={classes.inputField} type="number" placeholder="Enter expected price"/>}
            {isTouched &&<input className={classes.inputField} type="email" placeholder="Enter email ID"/>}
            {isTouched &&<div className={classes.wrapper}>
                <button className={classes.button} >GO</button>
            </div>}
        </form>
    </div>
}

export default Form
