import React from "react";

import './style.css'

const Toggler = (props) => {

  const onChangeHandler = () => {
    props.setDarkMode(!props.darkMode)
    console.log("Changed")

    document.getElementsByName('body')[0].classList.add('lightMode')

    // document.getElementsByClassName('body').classList.add("darkMode")
  }

    return (
        <div>
           <main>
            <section>
              <div class="toggle-btn" id="_1st-toggle-btn">
                <input type="checkbox" onClick={onChangeHandler}/>
                <span></span>
              </div>
            </section>
          </main>
        </div>
    )
}

export default Toggler
